CommonData.BannerCustomCSS:
* {
    box-sizing: border-box;
}

#onetrust-banner-sdk * {
    font-family: var(--font-shahid),sans-serif;
}

html[lang="ar"] #onetrust-banner-sdk {
    line-height: 1.5 !important;
}

html:not([lang="ar"]) #onetrust-banner-sdk {
    line-height: 1.25 !important;
}

#onetrust-banner-sdk {
    outline: none;
}

#onetrust-banner-sdk .ot-sdk-container {
    padding: 1em;
}

#onetrust-banner-sdk #onetrust-policy {
    margin: 0;
}

#onetrust-banner-sdk #onetrust-policy-title {
    margin-bottom: 0.6em !important;
    font-size: 1em !important;
    display: inline-block;
}

#onetrust-banner-sdk #onetrust-policy-text {
    font-size: 0.8em;
    margin-bottom: 0.5em;
}

#onetrust-banner-sdk:not(.ot-iab-2).vertical-align-content #onetrust-button-group-parent {
     position: static !important;
     padding: 0;
}

#onetrust-button-group-parent #onetrust-button-group button {
    border-radius: 32px;
    padding: 0.5em 1.2em;
    text-align: center;
    border: none;
    outline: none;
    opacity: 1;
}

#onetrust-consent-sdk #onetrust-banner-sdk .ot-link-btn {
    color: #00cc99;
    margin: 1.5em 0;
}

#onetrust-button-group-parent #onetrust-button-group #onetrust-accept-btn-handler {
    background-image: linear-gradient(to right, #00cc99 0%, #0099ff 100%);
}

#onetrust-button-group-parent #onetrust-button-group #onetrust-accept-btn-handler:hover {
    background-image: none;
    background-color: #00cc99;
}

@media only screen and (max-width: 425px) {
    #onetrust-button-group-parent #onetrust-button-group #onetrust-accept-btn-handler {
        margin: 1em 0 1em;
    }
}

@media only screen and (min-width: 426px) and (max-width: 1023px) {
    body {
        font-size: 0.75em;
    }

    #onetrust-banner-sdk #onetrust-group-container {
        padding: 1em;
    }

    #onetrust-banner-sdk.ot-iab-2 #onetrust-policy-title,
    #onetrust-banner-sdk.ot-iab-2 .ot-dpd-title {
        font-size: 1.25em;
    }

    #onetrust-banner-sdk #onetrust-button-group-parent {
        display: flex;
        padding: 0 1em !important;
        width: 100%;
        position: relative;
        margin-left: 0;
    }

    #onetrust-button-group-parent #onetrust-button-group #onetrust-pc-btn-handler {
        margin-bottom: 0;
        padding: 0;
    }
}

@media only screen and (min-width: 1024px) {
    #onetrust-banner-sdk {
        width: 90%;
        margin: 0 auto;
        border-top-left-radius: 12px;
        border-top-right-radius: 12px;
    }

    #onetrust-banner-sdk .ot-sdk-row {
        display: flex;
        justify-content: space-between;
        padding: 2em 2.25em;
        gap: 2.25em;
    }

    #onetrust-banner-sdk .ot-sdk-row::after {
        display: none;
    }

    #onetrust-banner-sdk #onetrust-group-container {
        flex: 1;
    }

    #onetrust-banner-sdk #onetrust-policy-title {
        font-size: 1.4em !important;
        margin-bottom: 0.66667em !important;
        width: fit-content;
    }

    #onetrust-banner-sdk #onetrust-policy-text {
        font-size: 1em !important;
    }

    #onetrust-banner-sdk.vertical-align-content #onetrust-button-group-parent {
        display: flex;
        align-items: center;
        position: static;
        transform: unset;
        width: 18.359375%;
    }

    #onetrust-button-group-parent #onetrust-button-group {
        width: 100%;
    }

    #onetrust-button-group-parent #onetrust-button-group button {
        width: 100%;
        margin-bottom: 1.5em;
        font-size: 0.9em;
        height: 2.286em;
    }
}
